/**
 * Confirm cancel of a tinymce dialog
 *
 * Author: Kristiyan
 * Date : 27/11/2019
 */

tinymce.PluginManager.add('confirmcancel', function (editor, url) {
	editor.addCommand('cmdConfirmCancel', function () {
    apprise("Please confirm that you wish to cancel - the changes you made won't be saved.", { 
      confirm: true, 
      textOk: "Yes, cancel", 
      textCancel: "No, don't"
    }, function () { editor.windowManager.close(); });
	});
});