/**
 * Includes date AND time into the editor
 * Author: kristiyan
 * Date: 30/06/2018
 */

tinymce.PluginManager.add('datetime', function(editor, url) {
    editor.addCommand('mceInsertDateTime', function(){
      editor.execCommand('mceInsertDate', false); 
      editor.execCommand('mceInsertContent', false, " ");
      editor.execCommand('mceInsertTime', false); 
    });
});